import React, { useState, useEffect } from "react";
import Board from "./components/Board.jsx";
import "./components/Game.css";

const Game = ({ gridSize, winStreak }) => {
  const [board, setBoard] = useState(
    Array(gridSize)
      .fill(null)
      .map(() => Array(gridSize).fill(null))
  );
  const [xIsNext, setXIsNext] = useState(true);
  const [winner, setWinner] = useState(null);

  useEffect(() => {
    document.documentElement.style.setProperty("--grid-size", gridSize);
  }, [gridSize]);

  const handleClick = (row, col) => {
    if (board[row][col] || winner) return;

    const newBoard = board.map((r, rowIndex) =>
      r.map((cell, colIndex) => {
        if (rowIndex === row && colIndex === col) {
          return xIsNext ? "X" : "O";
        }
        return cell;
      })
    );

    setBoard(newBoard);
    setXIsNext(!xIsNext);

    if (checkWin(newBoard, winStreak)) {
      setWinner(xIsNext ? "X" : "O");
    }
  };

  const checkWin = (board, winStreak) => {
    const directions = [
      [0, 1], // horizontal
      [1, 0], // vertical
      [1, 1], // diagonal \
      [1, -1], // diagonal /
    ];

    const isWinningLine = (line) => {
      return line.every((cell) => cell && cell === line[0]);
    };

    for (let row = 0; row < gridSize; row++) {
      for (let col = 0; col < gridSize; col++) {
        for (let [dx, dy] of directions) {
          const line = [];
          for (let i = 0; i < winStreak; i++) {
            const x = row + i * dx;
            const y = col + i * dy;
            if (x < 0 || x >= gridSize || y < 0 || y >= gridSize) break;
            line.push(board[x][y]);
          }
          if (line.length === winStreak && isWinningLine(line)) {
            return true;
          }
        }
      }
    }
    return false;
  };

  // Return JSX here
  return (
    <div>
      <h2>
        Grid Size: {gridSize}, Win Streak: {winStreak}
      </h2>
      <Board board={board} handleClick={handleClick} />
      {winner && <h2>Winner: {winner}</h2>}
    </div>
  );
};

export default Game;
