import React, { useState } from "react";
import "./Game.css";

const Game = ({ gridSize, winStreak }) => {
  const [board, setBoard] = useState(
    Array(gridSize)
      .fill(null)
      .map(() => Array(gridSize).fill(null))
  );
  const [xIsNext, setXIsNext] = useState(true);
  const [winner, setWinner] = useState(null);

  const handleClick = (row, col) => {
    if (board[row][col] || winner) return;

    const newBoard = board.map((r, rowIndex) =>
      r.map((c, j) => (i === row && j === col ? (xIsNext ? "X" : "O") : c))
    );
    setBoard(newBoard);
    setXIsNext(!xIsNext);

    if (checkWin(newBoard, row, col, xIsNext ? "X" : "O")) {
      setWinner(xIsNext ? "X" : "O");
    }
  };

  const checkWin = (board, row, col, player) => {
    const directions = [
      { dr: 1, dc: 0 }, // vertical
      { dr: 0, dc: 1 }, // horizontal
      { dr: 1, dc: 1 }, // diagonal down-right
      { dr: 1, dc: -1 }, // diagonal down-left
    ];

    for (const { dr, dc } of directions) {
      let count = 0;
      for (let i = -winStreak + 1; i < winStreak; i++) {
        const r = row + i * dr;
        const c = col + i * dc;
        if (
          r >= 0 &&
          r < gridSize &&
          c >= 0 &&
          c < gridSize &&
          board[r][c] === player
        ) {
          count++;
          if (count >= winStreak) return true;
        } else {
          count = 0;
        }
      }
    }
    return false;
  };

  return (
    <div>
      <h1>
        Grid Size: {gridSize}, Win Streak: {winStreak}
      </h1>
      <div className="board">
        {board.map((row, rowIndex) => (
          <div key={rowIndex} className="board-row">
            {row.map((cell, colIndex) => (
              <button
                key={colIndex}
                className="cell"
                onClick={() => handleClick(rowIndex, colIndex)}
              >
                {cell}
              </button>
            ))}
          </div>
        ))}
      </div>
      {winner && <h2>Winner: {winner}</h2>}
    </div>
  );
};

export default Game;
